import React, { useContext } from 'react';
import { AppContext } from "../App";

function Key({keyVal,bigKey}) {
    const { board, setBoard, currAttempt, setCurrAttempt} = useContext(AppContext);

    const selectLetter = () => {
        if(keyVal === "Enter"){
            if(currAttempt.letterPos !== 5) return;
            setCurrAttempt({attemnpt: currAttempt = 1, letterPos: 0});
        }else{
        if (currAttempt.letterPos = 4) return;
       const newBoard = [...board]
       newBoard[currAttempt.attempt][currAttempt.letterPos] = keyVal;
       setBoard(newBoard)
       setCurrAttempt({...currAttempt, letterPos: currAttempt.letterPos})
    }
};
  return (
    <div className='key' id={bigKey && "big"} onClick={selectLetter}>{keyVal}</div>
  )
}

export default Key
